package com.example.a37_lesson

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.a37_lesson.databinding.FragmentMainBinding
import java.text.FieldPosition

class MainFragment : Fragment() {

    private lateinit var binding: FragmentMainBinding
    private var personList = arrayListOf<Person>(
        Person("https://static.tvtropes.org/pmwiki/pub/images/abcb6534_7913_4eb1_a7a5_62b081ebc628.png","Rick Sanchez","Alive"),
        Person("https://static.tvtropes.org/pmwiki/pub/images/evilmortyseason5.png","Morty Smith","Alive"),
        Person("https://static.wikia.nocookie.net/rickandmorty/images/b/bc/Albert_Einstein.png/revision/latest?cb=20150730213810","Albert Einstein","Dead"),
        Person("data:image/jpeg;base64,","Jerry Smith","Alive"),
        Person("data:image/jpeg;base64,","Bet Smith","Alive"),
        Person("https://static.wikia.nocookie.net/ultimatepopculture/images/e/ee/Rick_and_Morty_characters_-_Summer_Smith.jpg/revision/latest?cb=20210628141730","Summer Smith","Alive")
    )


    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentMainBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val adapter = PersonAdapter(personList,this::onClick)
        binding.rvPersons.adapter = adapter
    }
    private fun onClick(person: Person){
        findNavController().navigate(MainFragmentDirections.actionMainFragmentToDetailFragment(person))
    }

}